import React, { useState } from 'react';
import { TextField, Button, MenuItem, Grid, Box, Typography, Snackbar } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const UpdatePatient = () => {
  const [patient, setPatient] = useState({
    name: '',
    phoneNo: '',
    email: '',
    gender: '',
    age: '',
    address: '',
  })
  const [message, setMessage] = useState('');
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const navigate =  useNavigate();

  const handleChange = (e)=>{
    setPatient({...patient, [e.target.name ]:e.target.value});
  }
  const handleSubmit=async(e)=>{
    try {
      const response =  await fetch('http://localhost:3001/add-new-patient',{
        method : 'POST',
        headers : {
          'Content-Type': 'application/json',
        }  ,
        body : JSON.stringify(patient),
      });
        const data = await response.json();
        if(response.status === 400){
          setMessage(data.message);
          setOpenSnackbar(true);
        }else if (response.status === 201){
          setMessage(data.message);
          setOpenSnackbar(true);
          console.log('Patient added successfully');
          navigate('/');
        }else{
          setMessage("Something went wrong. Please try again.");
          setOpenSnackbar(true);
        }
    }
    catch (error) {
      console.error('Error:', error);
      setMessage('Error connecting to server');
      setOpenSnackbar(true);
    }
  }
  return (
    <Box sx={{p : 4 , maxWidth : '100%' , margin :' 0 auto '}}>
    <Typography variant='h4 ' gutterBottom>Add New Patient </Typography>
    <form onSubmit={handleSubmit}>
     <Grid item xs={12}>
      <TextField fullWidth label="Name" name="name" value={patient.name} onChange={handleChange} required/>
     </Grid>
     <Grid item xs={12}>
      <TextField fullWidth label="Phone Number" name="phoneNo" value={patient.phoneNo} onChange={handleChange} required />
     </Grid>
     <Grid item xs={12}>
      <TextField fullWidth label="Email" name="email" value={patient.email} onChange={handleChange} required />
     </Grid>
     <Grid item xs={12}>
      <TextField fullWidth label="Age" name="age" value={patient.age} onChange={handleChange}  required/>
     </Grid>
     <Grid item xs={12}>
            <TextField fullWidth select label="Gender" name="gender" value={patient.gender} onChange={handleChange} >
              <MenuItem value="Male">Male</MenuItem>
              <MenuItem value="Female">Female</MenuItem>
              <MenuItem value="Other">Other</MenuItem>
            </TextField>
          </Grid>
     <Grid item xs={12}>
      <TextField fullWidth label="Address" name="address" value={patient.address} onChange={handleChange} required />
     </Grid>
     <Grid item xs={12}>
      <button type='submit' variant="contained" color='primary'>Submit</button>
     </Grid>
    </form>
    <Snackbar
        open={openSnackbar}
        autoHideDuration={4000}
        onClose={() => setOpenSnackbar(false)}
        message={message}
      />
    </Box>
  )
}

export default UpdatePatient
