import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Typography, Box, Container } from '@mui/material';

const UpdatePatient = () => {
    const [phoneNo, setPhoneNo] = useState('');
    const [patientData, setPatientData] = useState({
        name: '',
        age: '',
        gender: '',
        email: '',
        address: ''
    });
    const [message, setMessage] = useState('');
    const [isPatientFound, setIsPatientFound] = useState(false);

    // Handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setPatientData({ ...patientData, [name]: value });
    };

    // Fetch patient data based on phone number
    const searchPatient = async () => {
        try {
            const response = await axios.get(`http://localhost:3001/search-patient/${phoneNo}`);
            setPatientData(response.data.patient);
            setIsPatientFound(true);
            setMessage('');
        } catch (err) {
            setMessage('Patient not found');
            setIsPatientFound(false);
            setPatientData({
                name: '',
                age: '',
                gender: '',
                email: '',
                address: ''
            });
        }
    };

    // Update patient data with validation
    const updatePatient = async () => {
        if (!isPatientFound) {
            setMessage('Patient not found. Please search first.');
            return;
        }

        // Check if any field is empty
        if (!patientData.name || !patientData.age || !patientData.gender || !patientData.email || !patientData.address) {
            setMessage('All fields are required.');
            return;
        }

        try {
            const response = await axios.put('http://localhost:3001/update-patient', {
                phoneNo,
                ...patientData
            });
            setMessage(response.data.message);
        } catch (err) {
            setMessage('Error updating patient.');
        }
    };

    return (
        <Container maxWidth="sm">
            <Box sx={{ padding: 3, borderRadius: 1, boxShadow: 3 }}>
                <Typography variant="h4" gutterBottom>
                    Update Patient
                </Typography>

                <TextField
                    label="Enter phone number"
                    variant="outlined"
                    fullWidth
                    value={phoneNo}
                    onChange={(e) => setPhoneNo(e.target.value)}
                    sx={{ marginBottom: 2 }}
                />
                <Button
                    variant="contained"
                    color="primary"
                    fullWidth
                    onClick={searchPatient}
                    sx={{ marginBottom: 2 }}
                >
                    Search
                </Button>

                {message && (
                    <Typography variant="body1" color={isPatientFound ? "green" : "red"}>
                        {message}
                    </Typography>
                )}

                {isPatientFound && (
                    <form>
                        <TextField
                            label="Name"
                            variant="outlined"
                            fullWidth
                            name="name"
                            value={patientData.name}
                            onChange={handleChange}
                            sx={{ marginBottom: 2 }}
                        />
                        <TextField
                            label="Age"
                            variant="outlined"
                            fullWidth
                            name="age"
                            value={patientData.age}
                            onChange={handleChange}
                            sx={{ marginBottom: 2 }}
                        />
                        <TextField
                            label="Gender"
                            variant="outlined"
                            fullWidth
                            name="gender"
                            value={patientData.gender}
                            onChange={handleChange}
                            sx={{ marginBottom: 2 }}
                        />
                        <TextField
                            label="Email"
                            variant="outlined"
                            fullWidth
                            name="email"
                            value={patientData.email}
                            onChange={handleChange}
                            sx={{ marginBottom: 2 }}
                        />
                        <TextField
                            label="Address"
                            variant="outlined"
                            fullWidth
                            name="address"
                            value={patientData.address}
                            onChange={handleChange}
                            sx={{ marginBottom: 2 }}
                        />
                        <Button
                            variant="contained"
                            color="success"
                            fullWidth
                            onClick={updatePatient}
                        >
                            Update
                        </Button>
                    </form>
                )}
            </Box>
        </Container>
    );
};

export default UpdatePatient;
