import React, { useState } from 'react';
import { TextField, Button, MenuItem, Grid, Box, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const AddPatient = () => {
  const [patient, setPatient] = useState({
    name: '',
    phoneNo: '',
    email: '',
    gender: '',
    age: '',
    address: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setPatient({ ...patient, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/add-new-patient', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(patient),
      });

      const data = await response.json();

      if(response.status===400){
        alert(data.message);
        
      }else if(response.status===201){
        alert(data.message);
        console.log("Patient added successfully");
        
        navigate('/');
      } else {
        alert("Something went wrong. Please try again.");
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error connecting to server');
    }
  };

  return (
    <Box sx={{ p: 4, maxWidth: 600, margin: '0 auto' }}>
      <Typography variant="h4" gutterBottom>Add Patient</Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          {/* Name Field */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Name"
              name="name"
              value={patient.name}
              onChange={handleChange}
            />
          </Grid>
          {/* Phone Number Field */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Phone Number"
              name="phoneNo"
              value={patient.phoneNo}
              onChange={handleChange}
            />
          </Grid>
          {/* Email Field */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Email"
              name="email"
              value={patient.email}
              onChange={handleChange}
            />
          </Grid>
          {/* Gender Field */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              select
              label="Gender"
              name="gender"
              value={patient.gender}
              onChange={handleChange}
            >
              <MenuItem value="Male">Male</MenuItem>
              <MenuItem value="Female">Female</MenuItem>
              <MenuItem value="Other">Other</MenuItem>
            </TextField>
          </Grid>
          {/* Age Field */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Age"
              name="age"
              value={patient.age}
              onChange={handleChange}
            />
          </Grid>
          {/* Address Field */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Address"
              name="address"
              value={patient.address}
              onChange={handleChange}
            />
          </Grid>
          {/* Submit Button */}
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary">Submit</Button>
          </Grid>
        </Grid>
      </form>
      
    </Box>
    
  );
};

export default AddPatient;
