// src/Components/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import '../style.css'
const Home = () => {
  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100vh', 
      backgroundColor: '#f9f9f9' 
    }}>
      <h1 style={{ 
        fontSize: '2rem', 
        marginBottom: '2rem', 
        color: '#333' 
      }}>
        Welcome to the Patient Management System
      </h1>
      <div style={{ display: 'flex', gap: '1rem' }}>
        <Link to="/AddPatient" style={linkStyle}>
          Add New Patient
        </Link>
        <Link to="/update" style={linkStyle}>
          Update Existing Patient
        </Link>
      </div>
    </div>
  );
};

// Shared styling for the navigation buttons
const linkStyle = {
  display: 'inline-block',
  padding: '0.8rem 1.5rem',
  fontSize: '1rem',
  border: 'none',
  borderRadius: '5px',
  textDecoration: 'none',
  backgroundColor: '#007BFF',
  color: '#fff',
  textAlign: 'center',
  cursor: 'pointer',
  transition: 'all 0.3s ease',
};

// Add hover effects to buttons
linkStyle[':hover'] = {
  backgroundColor: '#0056b3',
};

export default Home;
